export const findAllBookingsByPet = (
  petId: number,
  options?: SecondParameter<typeof axiosInstance>
) => {
  return axiosInstance<BookingDto[]>(
    { url: `/bookings/allByPet/pets/${petId}`, method: "GET" },
    options
  )
}

